package com.xencio.controller;import com.xencio.entity.User;import com.xencio.service.UserService;import com.xencio.service.impl.UserServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("user")public class UserController {    @Autowired    UserService userService;    @GetMapping("/test")    public String helloWorld(){        return "Hello World!";    }    @GetMapping("/get")    public User getUser(Integer id) {        return userService.getById(id);    }}